	//DP1:
	public static  int shortestPath2Dp1(int[][] grid) {
        // write your code here
        if(grid==null || grid.length==0 || grid[0].length<=1) return -1;
        
        int n=grid.length, m = grid[0].length;
        int[][] dp = new int[n][m];
        
        int[] dx = new int[] { 1, -1, 2, -2};
        int[] dy = new int[] { -2, -2, -1, -1};
        
        for (int i=0; i<n; i++) {
            for (int j=0; j<m; j++) {
                dp[i][j] = 1000000;
            }
        }
        
        dp[0][0] = 0;
        
        for (int j=0 ; j<m; j++) {
            for (int i=0; i<n; i++) {
                
                for (int k=0; k<dx.length; k++) {
                    if (i+dx[k]<0 || i+dx[k]>=n || j+dy[k]<0 || j+dy[k]>=m || grid[i][j]==1) {
                        continue;
                    }
                
                    dp[i][j] = Math.min(dp[i][j], dp[i+dx[k]][j+dy[k]] + 1);
                    //System.out.println(i+"|"+j + " : " + dp[i][j]);
                }
            }
        }
        
        if (dp[n-1][m-1] >=1000000) return -1;
        
        return dp[n-1][m-1];
        
    }
	
	//Another way of DP2:
	public int shortestPath2(boolean[][] grid) {
        // Write your code here
        if (grid == null || grid.length == 0) return -1;
        int n = grid.length, m = grid[0].length;
        int[][] dp = new int[n][m];

        for (int i=0; i<n; i++) {
          for (int j=0; j<m; j++) {
            dp[i][j] = Integer.MAX_VALUE;
          }
        }
        dp[0][0] = 0;
        int[] dx = {-2, -1, 1, 2};
        int[] dy = {-1, -2, -2, -1}; 

        for (int j=1; j<m; j++) {
          for (int i=0; i<n; i++) {
            if (grid[i][j]) continue;
            for ( int k=0; k<dx.length; k++) {
              int x = i+dx[k];
              int y = j+dy[k];
              if (!valid(grid, x, y) || dp[x][y] == Integer.MAX_VALUE ) continue;
              dp[i][j] = Math.min(dp[i][j], dp[x][y]+1);
             // System.out.println(i+"|"+j+"|"+dp[i][j]+"|"+x+"|"+y);
            }
          }
        }

        return dp[n-1][m-1] == Integer.MAX_VALUE ? -1 : dp[n-1][m-1]; 
    }

    private boolean valid(boolean[][] grid, int x, int y) {
      int n = grid.length, m = grid[0].length;
      if (x<0 || x>=n || y<0 || y>=m || grid[x][y]) return false;

      return true;
    }
	
	public static  int shortestPath2Dfs(int[][] grid) {
        // write your code here
        if(grid==null || grid.length==0 || grid[0].length<=1) return -1;
        int[] res = new int[]{-1};
        /*
        int[] dx = new int[] {1, 2, -2, -1};
        int[] dy = new int[] {2, 1,  1,  2};
        */
        
        /*
        int[] dx = new int[] { 1, -1, 2, -2};
        int[] dy = new int[] { 2, 2, 1, 1};
        */
        int[] dx = new int[] { -2, 2, -1, 1};
        int[] dy = new int[] { 1, 1, 2, 2};
        
        if (dfs(grid, 0, 0, dx, dy, 0, res)) return res[0];
        
        return -1;
	}
	
	private static boolean dfs(int[][] grid, int i, int j, int[] dx, int[] dy, int step, int[] res) {
		int n=grid.length, m = grid[0].length;
        //System.out.println(i+"|"+j );
        
        if (i==n-1&&j==m-1) {
            res[0] = res[0]>0?Math.min(res[0], step) : step;
            return true;
        }
        
        //if (i<0 || j<0 || i>=n || j>=m || grid[i][j]) return;
        
        
        
        for (int k=0; k<dx.length; k++) {
            int x=i+dx[k], y=j+dy[k];
        	if (x<0 || y<0 || x>=n || y>=m || grid[x][y]==1) continue;
        	step++;
        	grid[x][y]=1;
            if (dfs(grid, x, y, dx, dy, step, res)) {
            	
            	return true;
            } else {
            	grid[x][y] = 0;
            	step--;
            }
        }
        
        return false;
	}
	
	//Another dfs implementation: time out at 28%
	private boolean dfs2(boolean[][] grid, int x, int y, int[] dx, int[] dy, int step, int[] res) {
	      int n = grid.length, m = grid[0].length;
	      if (x==n-1 && y==m-1) {
	        //if (step>0) {
	          res[0] = Math.min(res[0], step);
	          return true;
	        //}
	      }

	      for (int k=0; k<dx.length; k++) {
	        int i = x + dx[k];
	        int j = y + dy[k];

	        if (i<0 || i>=n || j<0 || j>=m || grid[i][j]) continue;
	        grid[i][j] = true;
	        if (dfs2(grid, i, j, dx, dy, step+1, res)) {
	          return true;
	        } else {
	          //grid[][y] = true;
	          grid[i][j] = false;
	        }
	      }
	      return false;
	    }

	//Seems bfs is slower than DP
	//Can use 1. Map<x*m+y, steps> , or a one D array to store location of the spot or a Point object
	public static  int shortestPath2QueueBfs(int[][] grid) {
        // write your code here
        if(grid==null || grid.length==0 || grid[0].length<=1) return -1;
        int res = Integer.MAX_VALUE;
        int n=grid.length, m = grid[0].length;
        int[] dx = new int[] { 1, -1, 2, -2};
        int[] dy = new int[] { 2, 2, 1, 1};
        Queue<int[]> que = new LinkedList<>();
        que.offer(new int[] {0, 0, 0});
        
        while(!que.isEmpty()) {
        	int[] cur = que.poll();
        	
        	int x=cur[0], y= cur[1], step=cur[2];
        	if (x==n-1&&y==m-1) res = Math.min(res, step);
        	
        	 for (int k=0; k<dx.length; k++) {
        		 int i=x+dx[k], j=y+dy[k];
        		 if (i<0 || j<0 || i>=n || j>=m || grid[i][j]==1) continue;
        		 grid[i][j]=1;
        		 que.offer(new int[]{i, j, step+1});
        	 }
        }
        
        if (res==Integer.MAX_VALUE) return -1;
        return res;
        
        
	}
	
	public int shortestPath2DPWithRollingArray(boolean[][] grid) {
        // Write your code here
        if(grid==null || grid.length==0 || grid[0]==null || grid[0].length<=1) return -1;
        
        int n=grid.length, m = grid[0].length;
        
        int[][] dp = new int[n][3];

        for (int i=1; i<n; i++) {   //Missing this part will error out !!!
            dp[i][0] = Integer.MAX_VALUE;
        }

        dp[0][0] = 0;
        int[] dx = {-2, -1, 1, 2};
        int[] dy = {-1, -2, -2, -1};

        for (int j=1; j<m; j++) {
            for (int i=0; i<n; i++) {
                dp[i][j%3] = Integer.MAX_VALUE;
                if (grid[i][j]) continue;   //Missing this part will error out !!!
                for (int k=0; k<dx.length; k++) {
                    int x = i+dx[k];
                    int y = j+dy[k];

                    if (x<0||x>=n||y<0||y>=m) continue;
                    if (dp[x][y%3] == Integer.MAX_VALUE) continue;
                    dp[i][j%3] = Math.min(dp[i][j%3], dp[x][y%3]+1);
                }
            }
        }

        if (dp[n-1][(m-1)%3] == Integer.MAX_VALUE) return -1;
        else return dp[n-1][(m-1)%3];
    }
