	public int shortestPath2DFSMemo(boolean[][] grid) {
        // Write your code here
        if(grid==null || grid.length==0 || grid[0].length<=1) return -1;
        
        int n=grid.length, m = grid[0].length;
        
        int[] dx = {2, 1, -1, -2};
        int[] dy = {1, 2, 2, 1};
        int[] res = new int[1];
        res[0] = Integer.MAX_VALUE;
        if (dfs(0, 0, dx, dy, 0, grid, new HashMap<>(), res)) return res[0];
        return -1;
    }

    private boolean dfs(int i, int j, int[] dx, int[] dy, int step, boolean[][] grid, Map<Integer, Boolean> memo, int[] res) {
        int n=grid.length, m = grid[0].length;
        int cur = i*m+j;
        if (i==n-1 && j==m-1) {
            res[0] = Math.min(res[0], step);
            //memo.put(cur, true);
            //System.out.println(memo);

            return true;
        }

        //System.out.println(i + "|" + j);
        if (memo.containsKey(cur)) return memo.get(cur);

        boolean tmp = false;

        for (int k=0; k<dx.length; k++) {
            int x = i+dx[k];
            int y = j+dy[k];

            if (x<0||x>=n||y<0||y>=m||grid[x][y]) continue;
            grid[x][y] = true;
            if (dfs(x, y, dx, dy, step+1, grid, memo, res)) tmp = true;
            grid[x][y] = false;
        }

        memo.put(cur, tmp);
        return tmp;
    }
